void LED_fillBuffer(void) {
	uint32_t i = 0, p1, p2;
	uint8_t bit, mask;
	for(uint8_t row = 0; row < SCAN_RATE; row++) {
		for(bit = 0; bit < BITS_PER_CHANNEL; bit++) {
			p1 = row * WIDTH;
			p2 = p1 + WIDTH * SCAN_RATE;
			mask = 1<<bit;
			for(uint8_t col = 0; col < WIDTH; col++) {
				//printf("row: %x, col: %x, bit: %x, p1: %x, p2: %x, mask:%x\r\n", row, col, bit, p1, p2, mask);
				buffer[i] =
					(((frame[p2].R & mask) >> bit) << 5) |
					(((frame[p2].G & mask) >> bit)  << 4) |
					(((frame[p2].B & mask) >> bit)  << 3) |
					(((frame[p1].R & mask) >> bit)  << 2) |
					(((frame[p1].G & mask) >> bit)  << 1) |
					(((frame[p1].B & mask) >> bit)  << 0);
				p1++;
				p2++;
				i++;
			}
		}
	}
}


void LED_displayFrame(void) {
	uint32_t i = 0;
	for(uint8_t row = 0; row < SCAN_RATE; row++) {

		HAL_GPIO_WritePin(LM_OE_GPIO_Port, LM_OE_Pin, GPIO_PIN_SET);
		LL_GPIO_WriteOutputPort(GPIOB, (LL_GPIO_ReadOutputPort(GPIOB) & ~0x1F) | row);

		for(uint8_t bit = 0; bit < BITS_PER_CHANNEL; bit++) {
			HAL_GPIO_WritePin(LM_LAT_GPIO_Port, LM_LAT_Pin, GPIO_PIN_RESET);
			HAL_GPIO_WritePin(LM_CLK_GPIO_Port, LM_CLK_Pin, GPIO_PIN_RESET);
			for(uint8_t col = 0; col < WIDTH; col++) {
				LL_GPIO_WriteOutputPort(LM_R1_GPIO_Port, buffer[i]);
				HAL_GPIO_WritePin(LM_CLK_GPIO_Port, LM_CLK_Pin, GPIO_PIN_SET);
				i++;
			}
			// latch in the data
			HAL_GPIO_WritePin(LM_LAT_GPIO_Port, LM_LAT_Pin, GPIO_PIN_SET);
			HAL_GPIO_WritePin(LM_OE_GPIO_Port, LM_OE_Pin, GPIO_PIN_RESET);

			// delay by xxxx * 1<<bit
			for(uint32_t di = 0; di < 1 * (1<<bit); di++) {
				asm("nop");
			}
		}
	}
}
